import React, { useState, useEffect } from 'react';
import './Dashboard.css';

const Dashboard = ({ user }) => {
  const [stats, setStats] = useState({
    totalFarms: 0,
    activeAlerts: 0,
    weeklyYield: 0,
    weatherStatus: 'loading'
  });

  const [recentActivities] = useState([
    { id: 1, action: 'Irrigation completed', farm: 'North Field', time: '2 hours ago' },
    { id: 2, action: 'Pest inspection scheduled', farm: 'South Field', time: '4 hours ago' },
    { id: 3, action: 'Harvest report generated', farm: 'East Field', time: '1 day ago' }
  ]);

  useEffect(() => {
    // Simulate loading dashboard data
    setTimeout(() => {
      setStats({
        totalFarms: 12,
        activeAlerts: 3,
        weeklyYield: 847,
        weatherStatus: 'sunny'
      });
    }, 1000);
  }, []);

  const StatCard = ({ title, value, unit, icon, color }) => (
    <div className={`stat-card ${color}`}>
      <div className="stat-icon">
        {icon}
      </div>
      <div className="stat-content">
        <h3>{title}</h3>
        <div className="stat-value">
          {value} <span className="stat-unit">{unit}</span>
        </div>
      </div>
    </div>
  );

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h2>Farm Overview</h2>
        <p>Monitor your farms and stay updated with real-time insights</p>
      </div>

      <div className="stats-grid">
        <StatCard
          title="Total Farms"
          value={stats.totalFarms}
          unit="farms"
          color="blue"
          icon={
            <svg viewBox="0 0 24 24" fill="currentColor">
              <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
            </svg>
          }
        />

        <StatCard
          title="Active Alerts"
          value={stats.activeAlerts}
          unit="alerts"
          color="red"
          icon={
            <svg viewBox="0 0 24 24" fill="currentColor">
              <path d="M1 21h22L12 2 1 21zm12-3h-2v-2h2v2zm0-4h-2v-4h2v4z"/>
            </svg>
          }
        />

        <StatCard
          title="Weekly Yield"
          value={stats.weeklyYield}
          unit="kg"
          color="green"
          icon={
            <svg viewBox="0 0 24 24" fill="currentColor">
              <path d="M9 11H7v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2v-9h-2v9H9v-9z"/>
              <path d="M20 4h-3V2a1 1 0 0 0-1-1H8a1 1 0 0 0-1 1v2H4a1 1 0 0 0 0 2h1v1a3 3 0 0 0 3 3h8a3 3 0 0 0 3-3V6h1a1 1 0 0 0 0-2zM9 4V3h6v1H9z"/>
            </svg>
          }
        />

        <StatCard
          title="Weather"
          value={stats.weatherStatus === 'loading' ? '...' : '24°C'}
          unit="sunny"
          color="yellow"
          icon={
            <svg viewBox="0 0 24 24" fill="currentColor">
              <path d="M6.05 4.14l-.39-.39a.993.993 0 0 0-1.4 0l-.01.01a.984.984 0 0 0 0 1.4l.39.39c.39.39 1.01.39 1.4 0l.01-.01a.984.984 0 0 0 0-1.4zM3.01 10.5H1.99c-.55 0-.99.44-.99.99s.44 1 .99 1h1.01c.55 0 .99-.44.99-.99s-.44-1-.98-1zm9-9.95H12c-.55 0-.99.44-.99.99s.44 1 .99 1h.01c.55 0 .99-.44.99-.99s-.44-1-.99-1zm7.74 3.21c-.39-.39-1.02-.39-1.41-.01l-.39.39a.984.984 0 0 0 0 1.4l.01.01c.39.39 1.02.39 1.4 0l.39-.39a.984.984 0 0 0 0-1.4zm-1.81 15.1l.39.39a.996.996 0 1 0 1.41-1.41l-.39-.39a.993.993 0 0 0-1.41 0c-.39.39-.39 1.02 0 1.41zM20 11.49v.01c0 .55.44.99.99.99H22c.55 0 .99-.44.99-.99s-.44-1-.99-1h-1.01c-.55 0-.99.44-.99.99zM12 5.5c-3.31 0-6 2.69-6 6s2.69 6 6 6 6-2.69 6-6-2.69-6-6-6zm-.01 16.95H12c.55 0 .99-.44.99-.99s-.44-1-.99-1h-.01c-.55 0-.99.44-.99.99s.44 1 .99 1zm-7.74-3.21c.39.39 1.02.39 1.41.01l.39-.39a.993.993 0 0 0 0-1.4l-.01-.01a.996.996 0 0 0-1.4 0l-.39.39c-.39.39-.39 1.01 0 1.4z"/>
            </svg>
          }
        />
      </div>

      <div className="dashboard-content">
        <div className="recent-activities">
          <h3>Recent Activities</h3>
          <div className="activities-list">
            {recentActivities.map(activity => (
              <div key={activity.id} className="activity-item">
                <div className="activity-icon">
                  <svg viewBox="0 0 24 24" fill="currentColor">
                    <path d="M9 16.2L4.8 12l-1.4 1.4L9 19 21 7l-1.4-1.4L9 16.2z"/>
                  </svg>
                </div>
                <div className="activity-content">
                  <div className="activity-action">{activity.action}</div>
                  <div className="activity-details">
                    {activity.farm} • {activity.time}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="quick-actions">
          <h3>Quick Actions</h3>
          <div className="actions-grid">
            <button className="action-button">
              <svg viewBox="0 0 24 24" fill="currentColor">
                <path d="M19 13h-6v6h-2v-6H5v-2h6V5h2v6h6v2z"/>
              </svg>
              Add Farm
            </button>
            <button className="action-button">
              <svg viewBox="0 0 24 24" fill="currentColor">
                <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
              </svg>
              Schedule Irrigation
            </button>
            <button className="action-button">
              <svg viewBox="0 0 24 24" fill="currentColor">
                <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8l-6-6zm4 18H6V4h7v5h5v11z"/>
              </svg>
              Generate Report
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
