import React, { useState, useEffect } from 'react';
import './Dashboard.css';

const Dashboard = ({ user }) => {
  const [dashboardData, setDashboardData] = useState({
    totalBales: 0,
    activeSensors: 0,
    criticalAlerts: [],
    averageTemperature: 0,
    averageMoisture: 0,
    recentSensorData: [],
    teamActivity: [],
    weather: {},
    temperatureHistory: [],
    moistureHistory: [],
    alertHistory: []
  });
  const [loading, setLoading] = useState(true);

  // Convert Fahrenheit to Celsius
  const fahrenheitToCelsius = (fahrenheit) => {
    return Math.round((fahrenheit - 32) * 5/9 * 10) / 10;
  };

  useEffect(() => {
    // Simulate API call with time-series data
    setTimeout(() => {
      const now = new Date();
      const temperatureHistory = [];
      const moistureHistory = [];
      const alertHistory = [];

      // Generate 24 hours of data points (in Celsius)
      for (let i = 23; i >= 0; i--) {
        const time = new Date(now.getTime() - i * 60 * 60 * 1000);
        const tempF = 65 + Math.random() * 15; // Generate in F then convert
        temperatureHistory.push({
          time: time.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
          value: fahrenheitToCelsius(tempF),
          timestamp: time
        });
        moistureHistory.push({
          time: time.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
          value: 10 + Math.random() * 8, // 10-18% range
          timestamp: time
        });
      }

      // Generate alert count history (last 7 days)
      for (let i = 6; i >= 0; i--) {
        const date = new Date(now.getTime() - i * 24 * 60 * 60 * 1000);
        alertHistory.push({
          date: date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
          critical: Math.floor(Math.random() * 5),
          warning: Math.floor(Math.random() * 8),
          timestamp: date
        });
      }

      setDashboardData({
        totalBales: 145,
        activeSensors: 12,
        criticalAlerts: [
          { id: 1, sensor: 'Sensor-A3', message: 'High temperature detected', level: 'critical', time: '10 min ago' },
          { id: 2, sensor: 'Sensor-B7', message: 'Moisture level too high', level: 'critical', time: '25 min ago' },
          { id: 3, sensor: 'Sensor-C2', message: 'Temperature rising rapidly', level: 'warning', time: '45 min ago' }
        ],
        averageTemperature: fahrenheitToCelsius(68.4),
        averageMoisture: 12.8,
        recentSensorData: [
          { sensor: 'Sensor-A1', temperature: fahrenheitToCelsius(65.2), moisture: 11.5, lastUpdate: '5 min ago', status: 'ok' },
          { sensor: 'Sensor-A2', temperature: fahrenheitToCelsius(72.1), moisture: 15.2, lastUpdate: '7 min ago', status: 'warning' },
          { sensor: 'Sensor-A3', temperature: fahrenheitToCelsius(78.9), moisture: 13.1, lastUpdate: '10 min ago', status: 'critical' },
          { sensor: 'Sensor-B1', temperature: fahrenheitToCelsius(64.8), moisture: 10.9, lastUpdate: '12 min ago', status: 'ok' }
        ],
        teamActivity: [
          { user: 'Sarah Johnson', action: 'Added new sensor deployment', time: '2 hours ago' },
          { user: 'Mike Chen', action: 'Updated alert thresholds', time: '4 hours ago' },
          { user: 'Alex Rivera', action: 'Generated monthly report', time: '6 hours ago' }
        ],
        weather: {
          temperature: fahrenheitToCelsius(75),
          humidity: 62,
          condition: 'Partly Cloudy',
          icon: '⛅',
          location: 'Springfield, IL'
        },
        temperatureHistory,
        moistureHistory,
        alertHistory
      });
      setLoading(false);
    }, 1000);
  }, []);

  const renderEnhancedLineChart = (data, color, label, unit, gradientId) => {
    if (!data || data.length === 0) return null;

    const maxValue = Math.max(...data.map(d => d.value));
    const minValue = Math.min(...data.map(d => d.value));
    const range = maxValue - minValue || 1;

    // Create smooth curve path using quadratic bezier curves
    const createSmoothPath = (points) => {
      if (points.length < 2) return '';

      let path = `M ${points[0].x} ${points[0].y}`;

      for (let i = 1; i < points.length; i++) {
        const prev = points[i - 1];
        const curr = points[i];
        const next = points[i + 1];

        if (next) {
          // Calculate control points for smooth curve
          const cp1x = prev.x + (curr.x - prev.x) * 0.5;
          const cp1y = prev.y;
          const cp2x = curr.x - (next.x - curr.x) * 0.5;
          const cp2y = curr.y;

          path += ` C ${cp1x} ${cp1y}, ${cp2x} ${cp2y}, ${curr.x} ${curr.y}`;
        } else {
          path += ` L ${curr.x} ${curr.y}`;
        }
      }
      return path;
    };

    const points = data.map((point, index) => ({
      x: 50 + (index / (data.length - 1)) * 500,
      y: 180 - ((point.value - minValue) / range) * 140
    }));

    const smoothPath = createSmoothPath(points);

    return (
      <div className="enhanced-chart-container large-chart">
        <div className="chart-header">
          <h3>{label}</h3>
          <div className="chart-stats">
            <span className="current-value">{data[data.length - 1]?.value.toFixed(1)}{unit}</span>
            <span className="trend-indicator">
              {data[data.length - 1]?.value > data[data.length - 2]?.value ? '↗️' : '↘️'}
            </span>
          </div>
        </div>
        <div className="chart-wrapper large-chart-wrapper">
          <svg width="100%" height="220" viewBox="0 0 600 220" className="enhanced-chart">
            <defs>
              <linearGradient id={gradientId} x1="0%" y1="0%" x2="0%" y2="100%">
                <stop offset="0%" stopColor={color} stopOpacity="0.4" />
                <stop offset="50%" stopColor={color} stopOpacity="0.2" />
                <stop offset="100%" stopColor={color} stopOpacity="0.05" />
              </linearGradient>
              <filter id="shadow">
                <feDropShadow dx="0" dy="2" stdDeviation="3" floodOpacity="0.2"/>
              </filter>
              <filter id="glow">
                <feGaussianBlur stdDeviation="3" result="coloredBlur"/>
                <feMerge> 
                  <feMergeNode in="coloredBlur"/>
                  <feMergeNode in="SourceGraphic"/>
                </feMerge>
              </filter>
            </defs>

            {/* Grid lines */}
            {[0, 55, 110, 165, 220].map(y => (
              <line 
                key={y} 
                x1="50" 
                y1={y} 
                x2="550" 
                y2={y} 
                stroke="#f1f5f9" 
                strokeWidth="1" 
              />
            ))}

            {/* Y-axis labels */}
            {[0, 1, 2, 3, 4].map(i => {
              const value = minValue + (range * i / 4);
              const y = 200 - (i * 35);
              return (
                <text 
                  key={i} 
                  x="35" 
                  y={y + 5} 
                  fontSize="12" 
                  fill="#64748b" 
                  textAnchor="end"
                >
                  {value.toFixed(1)}
                </text>
              );
            })}

            {/* Area under the curve */}
            <path
              d={`${smoothPath} L 550 200 L 50 200 Z`}
              fill={`url(#${gradientId})`}
            />

            {/* Main curved line */}
            <path
              d={smoothPath}
              fill="none"
              stroke={color}
              strokeWidth="4"
              strokeLinecap="round"
              strokeLinejoin="round"
              filter="url(#glow)"
            />

            {/* Data points */}
            {points.map((point, index) => (
              <g key={index}>
                <circle
                  cx={point.x}
                  cy={point.y}
                  r="6"
                  fill="white"
                  stroke={color}
                  strokeWidth="3"
                  filter="url(#shadow)"
                />
                <circle
                  cx={point.x}
                  cy={point.y}
                  r="3"
                  fill={color}
                />
              </g>
            ))}
          </svg>
        </div>
        <div className="chart-labels">
          <span>{data[0]?.time}</span>
          <span>{data[Math.floor(data.length / 2)]?.time}</span>
          <span>{data[data.length - 1]?.time}</span>
        </div>
      </div>
    );
  };

  const renderEnhancedBarChart = (data, title) => {
    const maxValue = Math.max(...data.map(d => d.critical + d.warning)) || 1;

    return (
      <div className="enhanced-chart-container large-chart">
        <div className="chart-header">
          <h3>{title}</h3>
          <div className="chart-summary">
            <span>Total: {data.reduce((sum, item) => sum + item.critical + item.warning, 0)}</span>
          </div>
        </div>
        <div className="enhanced-bar-chart large-chart-wrapper">
          <svg width="100%" height="220" viewBox="0 0 500 220">
            <defs>
              <filter id="barShadow">
                <feDropShadow dx="0" dy="2" stdDeviation="4" floodOpacity="0.3"/>
              </filter>
              <linearGradient id="criticalGradient" x1="0%" y1="0%" x2="0%" y2="100%">
                <stop offset="0%" stopColor="#f87171" />
                <stop offset="100%" stopColor="#ef4444" />
              </linearGradient>
              <linearGradient id="warningGradient" x1="0%" y1="0%" x2="0%" y2="100%">
                <stop offset="0%" stopColor="#fbbf24" />
                <stop offset="100%" stopColor="#f59e0b" />
              </linearGradient>
            </defs>

            {data.map((item, index) => {
              const barWidth = 50;
              const x = 70 + index * 60;
              const criticalHeight = (item.critical / maxValue) * 150;
              const warningHeight = (item.warning / maxValue) * 150;

              return (
                <g key={index}>
                  {/* Warning bar (bottom) */}
                  <rect
                    x={x}
                    y={180 - warningHeight}
                    width={barWidth}
                    height={warningHeight}
                    fill="url(#warningGradient)"
                    rx="4"
                    filter="url(#barShadow)"
                  />
                  {/* Critical bar (top) */}
                  <rect
                    x={x}
                    y={180 - warningHeight - criticalHeight}
                    width={barWidth}
                    height={criticalHeight}
                    fill="url(#criticalGradient)"
                    rx="4"
                    filter="url(#barShadow)"
                  />
                  {/* Date label */}
                  <text
                    x={x + barWidth/2}
                    y={205}
                    fontSize="12"
                    fill="#64748b"
                    textAnchor="middle"
                    fontWeight="500"
                  >
                    {item.date}
                  </text>
                </g>
              );
            })}
          </svg>
        </div>
        <div className="chart-legend">
          <div className="legend-item">
            <div className="legend-color critical"></div>
            <span>Critical</span>
          </div>
          <div className="legend-item">
            <div className="legend-color warning"></div>
            <span>Warning</span>
          </div>
        </div>
      </div>
    );
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'critical': return '#ef4444';
      case 'warning': return '#f59e0b';
      case 'ok': return '#10b981';
      default: return '#6b7280';
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>Loading dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard dashboard-padding">
      <div className="dashboard-header">
        <h1>Farm Dashboard</h1>
        <p>Welcome back, {user.name}. Here's your farm overview.</p>
      </div>

      <div className="metrics-grid">
        <div className="metric-card">
          <div className="metric-icon">🌾</div>
          <div className="metric-content">
            <h3>{dashboardData.totalBales}</h3>
            <p>Total Hay Bales</p>
          </div>
        </div>

        <div className="metric-card">
          <div className="metric-icon">📡</div>
          <div className="metric-content">
            <h3>{dashboardData.activeSensors}</h3>
            <p>Active Sensors</p>
          </div>
        </div>

        <div className="metric-card critical">
          <div className="metric-icon">⚠️</div>
          <div className="metric-content">
            <h3>{dashboardData.criticalAlerts.filter(alert => alert.level === 'critical').length}</h3>
            <p>Critical Alerts</p>
          </div>
        </div>

        <div className="metric-card">
          <div className="metric-icon">🌡️</div>
          <div className="metric-content">
            <h3>{dashboardData.averageTemperature}°C</h3>
            <p>Avg Temperature</p>
          </div>
        </div>

        <div className="metric-card">
          <div className="metric-icon">💧</div>
          <div className="metric-content">
            <h3>{dashboardData.averageMoisture}%</h3>
            <p>Avg Moisture</p>
          </div>
        </div>

        <div className="metric-card weather">
          <div className="metric-icon">{dashboardData.weather.icon}</div>
          <div className="metric-content">
            <h3>{dashboardData.weather.temperature}°C</h3>
            <p>{dashboardData.weather.condition}</p>
            <small>{dashboardData.weather.location}</small>
          </div>
        </div>
      </div>

      <div className="charts-grid large-charts">
        <div className="dashboard-card chart-card">
          {renderEnhancedLineChart(dashboardData.temperatureHistory, '#ef4444', 'Temperature Trend (24h)', '°C', 'tempGradient')}
        </div>

        <div className="dashboard-card chart-card">
          {renderEnhancedLineChart(dashboardData.moistureHistory, '#3b82f6', 'Moisture Trend (24h)', '%', 'moistureGradient')}
        </div>

        <div className="dashboard-card chart-card alert-chart">
          {renderEnhancedBarChart(dashboardData.alertHistory, 'Alert History (7 days)')}
        </div>
      </div>

      <div className="dashboard-grid">
        <div className="dashboard-card alerts-card">
          <div className="card-header">
            <h2>Critical Alerts</h2>
            <span className="alert-count">{dashboardData.criticalAlerts.length}</span>
          </div>
          <div className="alerts-list">
            {dashboardData.criticalAlerts.map(alert => (
              <div key={alert.id} className={`alert-item ${alert.level}`}>
                <div className="alert-content">
                  <div className="alert-sensor">{alert.sensor}</div>
                  <div className="alert-message">{alert.message}</div>
                </div>
                <div className="alert-time">{alert.time}</div>
              </div>
            ))}
          </div>
        </div>

        <div className="dashboard-card sensors-card">
          <div className="card-header">
            <h2>Recent Sensor Data</h2>
          </div>
          <div className="sensors-list">
            {dashboardData.recentSensorData.map((sensor, index) => (
              <div key={index} className="sensor-item">
                <div className="sensor-info">
                  <div className="sensor-name">{sensor.sensor}</div>
                  <div className="sensor-data">
                    <span>🌡️ {sensor.temperature}°C</span>
                    <span>💧 {sensor.moisture}%</span>
                  </div>
                </div>
                <div className="sensor-status">
                  <div 
                    className="status-indicator" 
                    style={{ backgroundColor: getStatusColor(sensor.status) }}
                  ></div>
                  <span className="last-update">{sensor.lastUpdate}</span>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="dashboard-card activity-card">
          <div className="card-header">
            <h2>Team Activity</h2>
          </div>
          <div className="activity-list">
            {dashboardData.teamActivity.map((activity, index) => (
              <div key={index} className="activity-item">
                <div className="activity-content">
                  <div className="activity-user">{activity.user}</div>
                  <div className="activity-action">{activity.action}</div>
                </div>
                <div className="activity-time">{activity.time}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
