// src/components/ProfileDropdown.js - Fixed Profile Picture Loading
import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { User, LogOut, ChevronDown } from 'lucide-react';
import './ProfileDropdown.css';

const ProfileDropdown = ({ user, onLogout }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState(user);
  const [imageError, setImageError] = useState(false);
  const dropdownRef = useRef(null);

  // Update currentUser when user prop changes
  useEffect(() => {
    setCurrentUser(user);
    setImageError(false); // Reset image error when user changes
  }, [user]);

  // Listen for user updates from Profile page
  useEffect(() => {
    const handleUserUpdate = (event) => {
      if (event.detail) {
        setCurrentUser(event.detail);
        setImageError(false);
        setIsOpen(false);
      }
    };

    window.addEventListener('userUpdated', handleUserUpdate);
    return () => window.removeEventListener('userUpdated', handleUserUpdate);
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    if (isOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isOpen]);

  const handleLogout = () => {
    setIsOpen(false);
    onLogout();
  };

  const getRoleLabel = (role) => {
    switch (role) {
      case 'admin': return 'Farm Owner';
      case 'manager': return 'Farm Manager';
      case 'worker': return 'Worker';
      default: return 'Farm Owner';
    }
  };

  const getInitials = (name) => {
    if (!name) return 'U';
    return name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
  };

  const handleImageError = () => {
    setImageError(true);
  };

  // Check if profile picture exists and is valid
  const hasValidProfilePicture = () => {
    if (!currentUser?.profilePicture) return false;
    if (imageError) return false;
    if (currentUser.profilePicture === '') return false;
    return true;
  };

  if (!currentUser) return null;

  return (
    <div className="profile-dropdown-container" ref={dropdownRef}>
      <button 
        className={`profile-button ${isOpen ? 'open' : ''}`}
        onClick={() => setIsOpen(!isOpen)}
        aria-expanded={isOpen}
        aria-label="User menu"
      >
        <div className="profile-avatar">
          {hasValidProfilePicture() ? (
            <img 
              src={currentUser.profilePicture} 
              alt={currentUser.name}
              onError={handleImageError}
            />
          ) : (
            <span className="avatar-initials">
              {getInitials(currentUser.name)}
            </span>
          )}
        </div>
        <div className="profile-info">
          <span className="profile-name">{currentUser.name || 'User'}</span>
          <span className="profile-role">{getRoleLabel(currentUser.role)}</span>
        </div>
        <ChevronDown size={14} className="chevron-icon" />
      </button>

      {isOpen && (
        <div className={`profile-dropdown ${isOpen ? 'open' : ''}`}>
          <div className="dropdown-user-info">
            <div className="dropdown-avatar">
              {hasValidProfilePicture() ? (
                <img 
                  src={currentUser.profilePicture} 
                  alt={currentUser.name}
                  onError={handleImageError}
                />
              ) : (
                <span className="avatar-initials-large">
                  {getInitials(currentUser.name)}
                </span>
              )}
            </div>
            <div className="dropdown-user-details">
              <div className="dropdown-user-name">{currentUser.name || 'User'}</div>
              <div className="dropdown-user-email">{currentUser.email || 'user@example.com'}</div>
              <div className="dropdown-user-role">{getRoleLabel(currentUser.role)}</div>
            </div>
          </div>

          <div className="dropdown-divider"></div>

          <div className="dropdown-menu">
            <Link 
              to="/my-profile" 
              className="dropdown-item"
              onClick={() => setIsOpen(false)}
            >
              <User size={18} />
              <span>My Profile</span>
            </Link>

            <div className="dropdown-divider"></div>

            <button 
              className="dropdown-item logout"
              onClick={handleLogout}
            >
              <LogOut size={18} />
              <span>Sign Out</span>
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfileDropdown;
