import React, { useState, useEffect } from 'react';
import './Dashboard.css';

const Dashboard = ({ user }) => {
  const [dashboardData, setDashboardData] = useState({
    totalBales: 0,
    activeSensors: 0,
    criticalAlerts: [],
    averageTemperature: 0,
    averageMoisture: 0,
    recentSensorData: [],
    teamActivity: [],
    weather: {},
    temperatureHistory: [],
    moistureHistory: [],
    alertHistory: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate API call with time-series data
    setTimeout(() => {
      const now = new Date();
      const temperatureHistory = [];
      const moistureHistory = [];
      const alertHistory = [];

      // Generate 24 hours of data points
      for (let i = 23; i >= 0; i--) {
        const time = new Date(now.getTime() - i * 60 * 60 * 1000);
        temperatureHistory.push({
          time: time.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
          value: 65 + Math.random() * 15, // 65-80¬∞F range
          timestamp: time
        });
        moistureHistory.push({
          time: time.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
          value: 10 + Math.random() * 8, // 10-18% range
          timestamp: time
        });
      }

      // Generate alert count history (last 7 days)
      for (let i = 6; i >= 0; i--) {
        const date = new Date(now.getTime() - i * 24 * 60 * 60 * 1000);
        alertHistory.push({
          date: date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
          critical: Math.floor(Math.random() * 5),
          warning: Math.floor(Math.random() * 8),
          timestamp: date
        });
      }

      setDashboardData({
        totalBales: 145,
        activeSensors: 12,
        criticalAlerts: [
          { id: 1, sensor: 'Sensor-A3', message: 'High temperature detected', level: 'critical', time: '10 min ago' },
          { id: 2, sensor: 'Sensor-B7', message: 'Moisture level too high', level: 'critical', time: '25 min ago' },
          { id: 3, sensor: 'Sensor-C2', message: 'Temperature rising rapidly', level: 'warning', time: '45 min ago' }
        ],
        averageTemperature: 68.4,
        averageMoisture: 12.8,
        recentSensorData: [
          { sensor: 'Sensor-A1', temperature: 65.2, moisture: 11.5, lastUpdate: '5 min ago', status: 'ok' },
          { sensor: 'Sensor-A2', temperature: 72.1, moisture: 15.2, lastUpdate: '7 min ago', status: 'warning' },
          { sensor: 'Sensor-A3', temperature: 78.9, moisture: 13.1, lastUpdate: '10 min ago', status: 'critical' },
          { sensor: 'Sensor-B1', temperature: 64.8, moisture: 10.9, lastUpdate: '12 min ago', status: 'ok' }
        ],
        teamActivity: [
          { user: 'Sarah Johnson', action: 'Added new sensor deployment', time: '2 hours ago' },
          { user: 'Mike Chen', action: 'Updated alert thresholds', time: '4 hours ago' },
          { user: 'Alex Rivera', action: 'Generated monthly report', time: '6 hours ago' }
        ],
        weather: {
          temperature: 75,
          humidity: 62,
          condition: 'Partly Cloudy',
          icon: '‚õÖ',
          location: 'Springfield, IL'
        },
        temperatureHistory,
        moistureHistory,
        alertHistory
      });
      setLoading(false);
    }, 1000);
  }, []);

  const renderLineChart = (data, color, label, unit) => {
    const maxValue = Math.max(...data.map(d => d.value));
    const minValue = Math.min(...data.map(d => d.value));
    const range = maxValue - minValue || 1;

    return (
      <div className="chart-container">
        <div className="chart-header">
          <h3>{label}</h3>
          <span className="chart-current">{data[data.length - 1]?.value.toFixed(1)}{unit}</span>
        </div>
        <div className="chart">
          <svg width="100%" height="120" viewBox="0 0 400 120">
            <defs>
              <linearGradient id={`gradient-${label}`} x1="0%" y1="0%" x2="0%" y2="100%">
                <stop offset="0%" stopColor={color} stopOpacity="0.3" />
                <stop offset="100%" stopColor={color} stopOpacity="0.05" />
              </linearGradient>
            </defs>

            {[0, 25, 50, 75, 100].map(y => (
              <line key={y} x1="0" y1={y} x2="400" y2={y} stroke="#f3f4f6" strokeWidth="1" />
            ))}

            <polyline
              fill="none"
              stroke={color}
              strokeWidth="2"
              points={data.map((point, index) => {
                const x = (index / (data.length - 1)) * 400;
                const y = 100 - ((point.value - minValue) / range) * 80;
                return `${x},${y}`;
              }).join(' ')}
            />

            <polygon
              fill={`url(#gradient-${label})`}
              points={[
                ...data.map((point, index) => {
                  const x = (index / (data.length - 1)) * 400;
                  const y = 100 - ((point.value - minValue) / range) * 80;
                  return `${x},${y}`;
                }),
                `400,100`,
                `0,100`
              ].join(' ')}
            />

            {data.map((point, index) => {
              const x = (index / (data.length - 1)) * 400;
              const y = 100 - ((point.value - minValue) / range) * 80;
              return (
                <circle
                  key={index}
                  cx={x}
                  cy={y}
                  r="3"
                  fill={color}
                />
              );
            })}
          </svg>
        </div>
        <div className="chart-labels">
          <span>{data[0]?.time}</span>
          <span>{data[Math.floor(data.length / 2)]?.time}</span>
          <span>{data[data.length - 1]?.time}</span>
        </div>
      </div>
    );
  };

  const renderBarChart = (data, title) => {
    const maxValue = Math.max(...data.map(d => d.critical + d.warning));

    return (
      <div className="chart-container">
        <div className="chart-header">
          <h3>{title}</h3>
        </div>
        <div className="bar-chart">
          {data.map((item, index) => (
            <div key={index} className="bar-group">
              <div className="bar-container">
                <div 
                  className="bar bar-critical" 
                  style={{ 
                    height: `${(item.critical / (maxValue || 1)) * 60}px`,
                    backgroundColor: '#ef4444'
                  }}
                ></div>
                <div 
                  className="bar bar-warning" 
                  style={{ 
                    height: `${(item.warning / (maxValue || 1)) * 60}px`,
                    backgroundColor: '#f59e0b'
                  }}
                ></div>
              </div>
              <div className="bar-label">{item.date}</div>
            </div>
          ))}
        </div>
        <div className="chart-legend">
          <span><div className="legend-color" style={{backgroundColor: '#ef4444'}}></div>Critical</span>
          <span><div className="legend-color" style={{backgroundColor: '#f59e0b'}}></div>Warning</span>
        </div>
      </div>
    );
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'critical': return '#ef4444';
      case 'warning': return '#f59e0b';
      case 'ok': return '#10b981';
      default: return '#6b7280';
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>Loading dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>HayGuard Dashboard</h1>
        <p>Welcome back, {user.name}. Here's your farm overview.</p>
      </div>

      <div className="metrics-grid">
        <div className="metric-card">
          <div className="metric-icon">üåæ</div>
          <div className="metric-content">
            <h3>{dashboardData.totalBales}</h3>
            <p>Total Hay Bales</p>
          </div>
        </div>

        <div className="metric-card">
          <div className="metric-icon">üì°</div>
          <div className="metric-content">
            <h3>{dashboardData.activeSensors}</h3>
            <p>Active Sensors</p>
          </div>
        </div>

        <div className="metric-card critical">
          <div className="metric-icon">‚ö†Ô∏è</div>
          <div className="metric-content">
            <h3>{dashboardData.criticalAlerts.filter(alert => alert.level === 'critical').length}</h3>
            <p>Critical Alerts</p>
          </div>
        </div>

        <div className="metric-card">
          <div className="metric-icon">üå°Ô∏è</div>
          <div className="metric-content">
            <h3>{dashboardData.averageTemperature}¬∞F</h3>
            <p>Avg Temperature</p>
          </div>
        </div>

        <div className="metric-card">
          <div className="metric-icon">üíß</div>
          <div className="metric-content">
            <h3>{dashboardData.averageMoisture}%</h3>
            <p>Avg Moisture</p>
          </div>
        </div>

        <div className="metric-card weather">
          <div className="metric-icon">{dashboardData.weather.icon}</div>
          <div className="metric-content">
            <h3>{dashboardData.weather.temperature}¬∞F</h3>
            <p>{dashboardData.weather.condition}</p>
            <small>{dashboardData.weather.location}</small>
          </div>
        </div>
      </div>

      <div className="charts-grid">
        <div className="dashboard-card chart-card">
          {renderLineChart(dashboardData.temperatureHistory, '#ef4444', 'Temperature Trend (24h)', '¬∞F')}
        </div>

        <div className="dashboard-card chart-card">
          {renderLineChart(dashboardData.moistureHistory, '#3b82f6', 'Moisture Trend (24h)', '%')}
        </div>

        <div className="dashboard-card chart-card">
          {renderBarChart(dashboardData.alertHistory, 'Alert History (7 days)')}
        </div>
      </div>

      <div className="dashboard-grid">
        <div className="dashboard-card alerts-card">
          <div className="card-header">
            <h2>Critical Alerts</h2>
            <span className="alert-count">{dashboardData.criticalAlerts.length}</span>
          </div>
          <div className="alerts-list">
            {dashboardData.criticalAlerts.map(alert => (
              <div key={alert.id} className={`alert-item ${alert.level}`}>
                <div className="alert-content">
                  <div className="alert-sensor">{alert.sensor}</div>
                  <div className="alert-message">{alert.message}</div>
                </div>
                <div className="alert-time">{alert.time}</div>
              </div>
            ))}
          </div>
        </div>

        <div className="dashboard-card sensors-card">
          <div className="card-header">
            <h2>Recent Sensor Data</h2>
          </div>
          <div className="sensors-list">
            {dashboardData.recentSensorData.map((sensor, index) => (
              <div key={index} className="sensor-item">
                <div className="sensor-info">
                  <div className="sensor-name">{sensor.sensor}</div>
                  <div className="sensor-data">
                    <span>üå°Ô∏è {sensor.temperature}¬∞F</span>
                    <span>üíß {sensor.moisture}%</span>
                  </div>
                </div>
                <div className="sensor-status">
                  <div 
                    className="status-indicator" 
                    style={{ backgroundColor: getStatusColor(sensor.status) }}
                  ></div>
                  <span className="last-update">{sensor.lastUpdate}</span>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="dashboard-card activity-card">
          <div className="card-header">
            <h2>Team Activity</h2>
          </div>
          <div className="activity-list">
            {dashboardData.teamActivity.map((activity, index) => (
              <div key={index} className="activity-item">
                <div className="activity-content">
                  <div className="activity-user">{activity.user}</div>
                  <div className="activity-action">{activity.action}</div>
                </div>
                <div className="activity-time">{activity.time}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
