// src/components/Sidebar.js - Logo Centered Vertically
import React, { useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Sensors.css';

// Icon components (inline SVG)
const Icons = {
  Dashboard: () => (
    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <rect x="3" y="3" width="7" height="7" />
      <rect x="14" y="3" width="7" height="7" />
      <rect x="14" y="14" width="7" height="7" />
      <rect x="3" y="14" width="7" height="7" />
    </svg>
  ),
  Sensors: () => (
    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M12 2v20M2 12h20" />
      <circle cx="12" cy="12" r="3" />
      <circle cx="12" cy="12" r="8" />
    </svg>
  ),
  Reports: () => (
    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M3 3v18h18" />
      <path d="M18 17V9M14 17v-5M10 17v-3M6 17v-1" />
    </svg>
  ),
  Alerts: () => (
    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M10.29 3.86L1.82 18a2 2 0 001.71 3h16.94a2 2 0 001.71-3L13.71 3.86a2 2 0 00-3.42 0z" />
      <line x1="12" y1="9" x2="12" y2="13" />
      <line x1="12" y1="17" x2="12.01" y2="17" />
    </svg>
  ),
  Team: () => (
    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
      <circle cx="9" cy="7" r="4" />
      <path d="M23 21v-2a4 4 0 00-3-3.87M16 3.13a4 4 0 010 7.75" />
    </svg>
  ),
  Farm: () => (
    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M3 9l9-7 9 7v11a2 2 0 01-2 2H5a2 2 0 01-2-2z" />
      <polyline points="9 22 9 12 15 12 15 22" />
    </svg>
  ),
  HayGuard: () => (
    <svg viewBox="0 0 64 64" fill="none">
      <path 
        d="M32 8L24 20L32 28L40 20L32 8Z" 
        fill="#8B4513"
      />
      <path 
        d="M16 24L8 36L16 44L24 36L16 24Z" 
        fill="#F4C430"
      />
      <path 
        d="M48 24L40 36L48 44L56 36L48 24Z" 
        fill="#F4C430"
      />
      <path 
        d="M32 40L24 52L32 60L40 52L32 40Z" 
        fill="#8B4513"
      />
      <circle cx="32" cy="32" r="6" fill="#8B4513" />
    </svg>
  ),
  ChevronLeft: () => (
    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5">
      <polyline points="15 18 9 12 15 6" />
    </svg>
  )
};

const Sidebar = ({ collapsed, onToggle, user }) => {
  const location = useLocation();

  const navigationItems = [
    { path: '/dashboard', icon: 'Dashboard', label: 'Dashboard' },
    { path: '/sensors', icon: 'Sensors', label: 'Sensors' },
    { path: '/reports', icon: 'Reports', label: 'Reports' },
    { path: '/alerts', icon: 'Alerts', label: 'Alerts' },
    { path: '/team', icon: 'Team', label: 'Team' }
  ];

  // Keyboard shortcut: Ctrl+B to toggle sidebar
  useEffect(() => {
    const handleKeyPress = (e) => {
      if ((e.ctrlKey || e.metaKey) && e.key === 'b') {
        e.preventDefault();
        onToggle();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [onToggle]);

  // Click anywhere on collapsed sidebar to expand
  const handleSidebarClick = () => {
    if (collapsed) {
      onToggle();
    }
  };

  return (
    <aside 
      className={`sidebar ${collapsed ? 'collapsed' : ''}`}
      onClick={handleSidebarClick}
    >
      {/* Header with Logo - Centered Vertically */}
      <div className="sidebar-header">
        <div className="sidebar-logo">
          <div className="logo-icon">
            <Icons.HayGuard />
          </div>
          <span className="logo-text">HAYGUARD</span>
        </div>

        <button 
          className="sidebar-toggle"
          onClick={(e) => {
            e.stopPropagation();
            onToggle();
          }}
          aria-label={collapsed ? 'Expand sidebar' : 'Collapse sidebar'}
          title={collapsed ? 'Expand (Ctrl+B)' : 'Collapse (Ctrl+B)'}
        >
          <Icons.ChevronLeft />
        </button>
      </div>

      {/* Navigation Items */}
      <nav className="sidebar-nav">
        {navigationItems.map((item) => {
          const Icon = Icons[item.icon];
          const isActive = location.pathname === item.path || 
                          (item.path === '/dashboard' && location.pathname === '/');

          return (
            <Link
              key={item.path}
              to={item.path}
              className={`nav-item ${isActive ? 'active' : ''}`}
              data-tooltip={item.label}
              onClick={(e) => e.stopPropagation()}
            >
              <Icon />
              <span className="nav-label">{item.label}</span>
            </Link>
          );
        })}
      </nav>

      {/* Farm Profile Footer */}
      <div className="sidebar-footer">
        <Link 
          to="/my-farm" 
          className="farm-profile-link"
          onClick={(e) => e.stopPropagation()}
        >
          <div className="farm-icon">
            <Icons.Farm />
          </div>
          <div className="farm-info">
            <div className="farm-name">Greenfield Farm</div>
            <div className="farm-location">Farmington, IA</div>
            <div className="farm-action">
              View My Farm Profile â†’
            </div>
          </div>
        </Link>
      </div>
    </aside>
  );
};

export default Sidebar;
