import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Sidebar.css';

const Sidebar = ({ collapsed, onToggle, user }) => {
  const location = useLocation();

  const menuItems = [
    {
      icon: 'dashboard',
      label: 'Dashboard',
      path: '/dashboard'
    },
    {
      icon: 'farm',
      label: 'Farms',
      path: '/farms'
    },
    {
      icon: 'analytics',
      label: 'Analytics',
      path: '/analytics'
    },
    {
      icon: 'reports',
      label: 'Reports',
      path: '/reports'
    },
    {
      icon: 'team',
      label: 'Team',
      path: '/team'
    },
    {
      icon: 'weather',
      label: 'Weather',
      path: '/weather'
    },
    {
      icon: 'alerts',
      label: 'Alerts',
      path: '/alerts'
    },
    {
      icon: 'settings',
      label: 'Settings',
      path: '/settings'
    }
  ];

  const getIconSvg = (iconName) => {
    switch (iconName) {
      case 'dashboard':
        return (
          <svg viewBox="0 0 24 24" fill="currentColor">
            <path d="M3 13h8V3H3v10zm0 8h8v-6H3v6zm10 0h8V11h-8v10zm0-18v6h8V3h-8z"/>
          </svg>
        );
      case 'farm':
        return (
          <svg viewBox="0 0 24 24" fill="currentColor">
            <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
            <path d="M19 7h-3V6a4 4 0 0 0-8 0v1H5a1 1 0 0 0-1 1v11a3 3 0 0 0 3 3h10a3 3 0 0 0 3-3V8a1 1 0 0 0-1-1zM10 6a2 2 0 0 1 4 0v1h-4V6zm8 13a1 1 0 0 1-1 1H7a1 1 0 0 1-1-1V9h2v1a1 1 0 0 0 2 0V9h4v1a1 1 0 0 0 2 0V9h2v10z"/>
          </svg>
        );
      case 'analytics':
        return (
          <svg viewBox="0 0 24 24" fill="currentColor">
            <path d="M19 3H5a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2V5a2 2 0 0 0-2-2zM9 17H7v-7h2v7zm4 0h-2V7h2v10zm4 0h-2v-4h2v4z"/>
          </svg>
        );
      case 'reports':
        return (
          <svg viewBox="0 0 24 24" fill="currentColor">
            <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8l-6-6zm4 18H6V4h7v5h5v11z"/>
          </svg>
        );
      case 'team':
        return (
          <svg viewBox="0 0 24 24" fill="currentColor">
            <path d="M16 13c1.1 0 2-.9 2-2s-.9-2-2-2-2 .9-2 2 .9 2 2 2zm0 1c-1.33 0-4 .67-4 2v2h8v-2c0-1.33-2.67-2-4-2zM8 13c1.1 0 2-.9 2-2s-.9-2-2-2-2 .9-2 2 .9 2 2 2zm0 1c-1.33 0-4 .67-4 2v2h8v-2c0-1.33-2.67-2-4-2z"/>
          </svg>
        );
      case 'weather':
        return (
          <svg viewBox="0 0 24 24" fill="currentColor">
            <path d="M6.05 13.05L4.95 12c-.39-.39-.39-1.02 0-1.41s1.02-.39 1.41 0L7.5 11.73c.39.39.39 1.02 0 1.41s-1.02.39-1.41 0l-.04-.09zM19.95 11.05L18.85 12c-.39.39-1.02.39-1.41 0s-.39-1.02 0-1.41L18.58 9.45c.39-.39 1.02-.39 1.41 0s.39 1.02 0 1.41l-.04.19zM6.05 7.05L4.95 6c-.39-.39-.39-1.02 0-1.41s1.02-.39 1.41 0L7.5 5.73c.39.39.39 1.02 0 1.41s-1.02.39-1.41 0l-.04-.09z"/>
            <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-1 18h2v-2h-2v2zm1-4c-2.21 0-4-1.79-4-4s1.79-4 4-4 4 1.79 4 4-1.79 4-4 4z"/>
          </svg>
        );
      case 'alerts':
        return (
          <svg viewBox="0 0 24 24" fill="currentColor">
            <path d="M12 22c1.1 0 2-.9 2-2h-4c0 1.1.89 2 2 2zm6-6v-5c0-3.07-1.64-5.64-4.5-6.32V4c0-.83-.67-1.5-1.5-1.5s-1.5.67-1.5 1.5v.68C7.63 5.36 6 7.92 6 11v5l-2 2v1h16v-1l-2-2z"/>
          </svg>
        );
      case 'settings':
        return (
          <svg viewBox="0 0 24 24" fill="currentColor">
            <path d="M19.14,12.94c0.04-0.3,0.06-0.61,0.06-0.94c0-0.32-0.02-0.64-0.07-0.94l2.03-1.58c0.18-0.14,0.23-0.41,0.12-0.61 l-1.92-3.32c-0.12-0.22-0.37-0.29-0.59-0.22l-2.39,0.96c-0.5-0.38-1.03-0.7-1.62-0.94L14.4,2.81c-0.04-0.24-0.24-0.41-0.48-0.41 h-3.84c-0.24,0-0.43,0.17-0.47,0.41L9.25,5.35C8.66,5.59,8.12,5.92,7.63,6.29L5.24,5.33c-0.22-0.08-0.47,0-0.59,0.22L2.74,8.87 C2.62,9.08,2.66,9.34,2.86,9.48l2.03,1.58C4.84,11.36,4.8,11.69,4.8,12s0.02,0.64,0.07,0.94l-2.03,1.58 c-0.18,0.14-0.23,0.41-0.12,0.61l1.92,3.32c0.12,0.22,0.37,0.29,0.59,0.22l2.39-0.96c0.5,0.38,1.03,0.7,1.62,0.94l0.36,2.54 c0.05,0.24,0.24,0.41,0.48,0.41h3.84c0.24,0,0.44-0.17,0.47-0.41l0.36-2.54c0.59-0.24,1.13-0.56,1.62-0.94l2.39,0.96 c0.22,0.08,0.47,0,0.59-0.22l1.92-3.32c0.12-0.22,0.07-0.47-0.12-0.61L19.14,12.94z M12,15.6c-1.98,0-3.6-1.62-3.6-3.6 s1.62-3.6,3.6-3.6s3.6,1.62,3.6,3.6S13.98,15.6,12,15.6z"/>
          </svg>
        );
      default:
        return null;
    }
  };

  return (
    <div className={`sidebar ${collapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        <div className="logo">
          <div className="logo-icon">
            <svg viewBox="0 0 100 100" fill="currentColor">
              <path d="M50 10 L30 40 L50 40 L50 60 L70 30 L50 30 L50 10 Z" fill="#F4C430"/>
              <circle cx="50" cy="75" r="8" fill="#8B4513"/>
              <path d="M20 85 Q50 75 80 85 Q50 95 20 85 Z" fill="#F4C430"/>
            </svg>
          </div>
          {!collapsed && <span className="logo-text">HayGuard</span>}
        </div>

        <button className="collapse-btn" onClick={onToggle}>
          <svg viewBox="0 0 24 24" fill="currentColor">
            <path d={collapsed ? "M9 18l6-6-6-6" : "M15 18l-6-6 6-6"}/>
          </svg>
        </button>
      </div>

      <nav className="sidebar-nav">
        <ul>
          {menuItems.map((item) => (
            <li key={item.path}>
              <Link 
                to={item.path}
                className={`nav-link ${location.pathname === item.path ? 'active' : ''}`}
                title={collapsed ? item.label : ''}
              >
                <div className="nav-icon">
                  {getIconSvg(item.icon)}
                </div>
                {!collapsed && <span className="nav-label">{item.label}</span>}
              </Link>
            </li>
          ))}
        </ul>
      </nav>

      {!collapsed && (
        <div className="sidebar-footer">
          <div className="user-info">
            <div className="user-avatar">
              {user?.name?.charAt(0) || 'U'}
            </div>
            <div className="user-details">
              <div className="user-name">{user?.name || 'User'}</div>
              <div className="user-role">{user?.role || 'Farmer'}</div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
