import React, { useState } from 'react';
import './Login.css';

const Login = ({ onLogin }) => {
  const [credentials, setCredentials] = useState({
    email: '',
    password: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (field, value) => {
    setCredentials(prev => ({
      ...prev,
      [field]: value
    }));
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      await new Promise(resolve => setTimeout(resolve, 1000));

      if (credentials.email === 'demo@hayguard.com' && credentials.password === 'demo123') {
        const result = await onLogin(credentials.email, credentials.password);
        if (!result.success) {
          setError(result.error);
        }
      } else {
        setError('Invalid email or password. Use demo@hayguard.com / demo123 for demo access.');
      }
    } catch (err) {
      setError('Login failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleDemoLogin = () => {
    setCredentials({
      email: 'demo@hayguard.com',
      password: 'demo123'
    });
  };

  return (
    <div className="login-container">
      <div className="login-background">
        <div className="login-overlay"></div>
      </div>

      <div className="login-content">
        <div className="login-card">
          <div className="login-header">
            <div className="logo-container">
              <img src="/logo.png" alt="HayGuard" className="login-logo" />
            </div>
            <h1 className="login-title">Welcome to HayGuard</h1>
            <p className="login-subtitle">Smart hay monitoring for modern farmers</p>
          </div>

          <div className="demo-banner">
            <div className="demo-icon">üéØ</div>
            <div className="demo-content">
              <h3>Demo Access</h3>
              <p>Try HayGuard with our demo account</p>
              <div className="demo-credentials">
                <div><strong>Email:</strong> demo@hayguard.com</div>
                <div><strong>Password:</strong> demo123</div>
              </div>
              <button className="demo-fill-btn" onClick={handleDemoLogin}>
                Fill Demo Credentials
              </button>
            </div>
          </div>

          <form className="login-form" onSubmit={handleSubmit}>
            {error && (
              <div className="error-message">
                <span className="error-icon">‚ö†Ô∏è</span>
                {error}
              </div>
            )}

            <div className="form-group">
              <label htmlFor="email">Email Address</label>
              <input
                id="email"
                type="email"
                value={credentials.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                placeholder="Enter your email"
                required
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                id="password"
                type="password"
                value={credentials.password}
                onChange={(e) => handleInputChange('password', e.target.value)}
                placeholder="Enter your password"
                required
                className="form-input"
              />
            </div>

            <button 
              type="submit" 
              disabled={isLoading || !credentials.email || !credentials.password}
              className={`login-btn ${isLoading ? 'loading' : ''}`}
            >
              {isLoading ? (
                <>
                  <div className="spinner"></div>
                  Signing In...
                </>
              ) : (
                'Sign In to Dashboard'
              )}
            </button>
          </form>

          <div className="login-footer">
            <button type="button" className="forgot-link">Forgot your password?</button>
          </div>

          <div className="features-preview">
            <h4>What you'll get access to:</h4>
            <div className="features-grid">
              <div className="feature-item">
                <span className="feature-icon">üìä</span>
                <span>Real-time monitoring</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üå°Ô∏è</span>
                <span>Temperature & moisture analytics</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">‚ö†Ô∏è</span>
                <span>Smart alert system</span>
              </div>
              <div className="feature-item">
                <span className="feature-icon">üë•</span>
                <span>Team collaboration</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
