import React, { useState } from 'react';

const SensorManagement = () => {
  const [sensors, setSensors] = useState([
    {
      id: 1,
      name: 'TEMP-001',
      type: 'Temperature',
      location: 'Barn A - Section 1',
      status: 'active',
      batteryLevel: 87,
      lastReading: 23.5,
      unit: '°C',
      lastUpdate: '2 min ago',
      installDate: '2024-01-15',
      alertsCount: 0,
      dailyReadings: 1440, // readings per day
      coordinates: { x: 10, y: 15 }
    },
    {
      id: 2,
      name: 'MOIST-002',
      type: 'Moisture',
      location: 'Barn A - Section 2',
      status: 'active',
      batteryLevel: 65,
      lastReading: 12.3,
      unit: '%',
      lastUpdate: '5 min ago',
      installDate: '2024-01-20',
      alertsCount: 2,
      dailyReadings: 1440,
      coordinates: { x: 25, y: 15 }
    },
    {
      id: 3,
      name: 'TEMP-003',
      type: 'Temperature',
      location: 'Barn B - Section 1',
      status: 'warning',
      batteryLevel: 23,
      lastReading: 28.7,
      unit: '°C',
      lastUpdate: '1 min ago',
      installDate: '2024-02-01',
      alertsCount: 1,
      dailyReadings: 1440,
      coordinates: { x: 45, y: 30 }
    },
    {
      id: 4,
      name: 'MOIST-004',
      type: 'Moisture',
      location: 'Barn B - Section 2',
      status: 'offline',
      batteryLevel: 0,
      lastReading: 15.8,
      unit: '%',
      lastUpdate: '2 hours ago',
      installDate: '2024-02-05',
      alertsCount: 5,
      dailyReadings: 0,
      coordinates: { x: 60, y: 30 }
    }
  ]);

  const [showAddForm, setShowAddForm] = useState(false);
  const [editingSensor, setEditingSensor] = useState(null);
  const [selectedSensor, setSelectedSensor] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    type: 'Temperature',
    location: '',
    coordinates: { x: 0, y: 0 }
  });

  const handleInputChange = (field, value) => {
    if (field.includes('.')) {
      const [parent, child] = field.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [field]: value
      }));
    }
  };

  const handleAddSensor = () => {
    const newSensor = {
      id: sensors.length + 1,
      ...formData,
      status: 'active',
      batteryLevel: 100,
      lastReading: 0,
      unit: formData.type === 'Temperature' ? '°C' : '%',
      lastUpdate: 'Just added',
      installDate: new Date().toISOString().split('T')[0],
      alertsCount: 0,
      dailyReadings: 1440
    };

    setSensors(prev => [...prev, newSensor]);
    setFormData({ name: '', type: 'Temperature', location: '', coordinates: { x: 0, y: 0 } });
    setShowAddForm(false);
  };

  const handleEditSensor = (sensor) => {
    setEditingSensor(sensor.id);
    setFormData({
      name: sensor.name,
      type: sensor.type,
      location: sensor.location,
      coordinates: sensor.coordinates
    });
  };

  const handleUpdateSensor = () => {
    setSensors(prev => prev.map(sensor => 
      sensor.id === editingSensor 
        ? { ...sensor, ...formData }
        : sensor
    ));
    setEditingSensor(null);
    setFormData({ name: '', type: 'Temperature', location: '', coordinates: { x: 0, y: 0 } });
  };

  const handleDeleteSensor = (sensorId) => {
    if (window.confirm('Are you sure you want to delete this sensor?')) {
      setSensors(prev => prev.filter(sensor => sensor.id !== sensorId));
    }
  };

  const cancelEdit = () => {
    setEditingSensor(null);
    setShowAddForm(false);
    setFormData({ name: '', type: 'Temperature', location: '', coordinates: { x: 0, y: 0 } });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return '#10b981';
      case 'warning': return '#f59e0b';
      case 'offline': return '#ef4444';
      default: return '#6b7280';
    }
  };

  const getBatteryColor = (level) => {
    if (level > 50) return '#10b981';
    if (level > 20) return '#f59e0b';
    return '#ef4444';
  };

  const generateSensorStats = (sensor) => {
    const days = Math.floor((new Date() - new Date(sensor.installDate)) / (1000 * 60 * 60 * 24));
    const totalReadings = days * sensor.dailyReadings;
    const accuracy = 98.5 + Math.random() * 1.5;

    return {
      daysOnline: days,
      totalReadings: totalReadings,
      accuracy: accuracy.toFixed(1),
      avgDaily: sensor.dailyReadings,
      uptime: sensor.status === 'offline' ? (95 + Math.random() * 4).toFixed(1) : (99 + Math.random() * 1).toFixed(1)
    };
  };

  return (
    <div style={{ padding: '32px 24px', maxWidth: '1400px', margin: '0 auto' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '32px' }}>
        <div>
          <h1>Sensor Management</h1>
          <p style={{ color: '#6b7280' }}>Monitor and manage your farm sensors</p>
        </div>
        <button 
          onClick={() => setShowAddForm(!showAddForm)}
          style={{ 
            background: '#10b981', 
            color: 'white', 
            padding: '12px 24px', 
            border: 'none', 
            borderRadius: '8px', 
            cursor: 'pointer',
            fontSize: '14px',
            fontWeight: '500'
          }}
        >
          + Add New Sensor
        </button>
      </div>

      {/* Sensor Statistics Overview */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '20px', marginBottom: '32px' }}>
        <div style={{ background: 'white', padding: '20px', borderRadius: '12px', border: '1px solid #e5e7eb', textAlign: 'center' }}>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#3b82f6', marginBottom: '8px' }}>
            {sensors.length}
          </div>
          <div style={{ color: '#6b7280', fontSize: '14px' }}>Total Sensors</div>
        </div>
        <div style={{ background: 'white', padding: '20px', borderRadius: '12px', border: '1px solid #e5e7eb', textAlign: 'center' }}>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#10b981', marginBottom: '8px' }}>
            {sensors.filter(s => s.status === 'active').length}
          </div>
          <div style={{ color: '#6b7280', fontSize: '14px' }}>Active</div>
        </div>
        <div style={{ background: 'white', padding: '20px', borderRadius: '12px', border: '1px solid #e5e7eb', textAlign: 'center' }}>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#f59e0b', marginBottom: '8px' }}>
            {sensors.filter(s => s.status === 'warning').length}
          </div>
          <div style={{ color: '#6b7280', fontSize: '14px' }}>Warning</div>
        </div>
        <div style={{ background: 'white', padding: '20px', borderRadius: '12px', border: '1px solid #e5e7eb', textAlign: 'center' }}>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#ef4444', marginBottom: '8px' }}>
            {sensors.filter(s => s.status === 'offline').length}
          </div>
          <div style={{ color: '#6b7280', fontSize: '14px' }}>Offline</div>
        </div>
      </div>

      {/* Add/Edit Form */}
      {(showAddForm || editingSensor) && (
        <div style={{ 
          background: 'white', 
          padding: '24px', 
          borderRadius: '12px', 
          border: '1px solid #e5e7eb', 
          marginBottom: '24px',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
        }}>
          <h2>{editingSensor ? 'Edit Sensor' : 'Add New Sensor'}</h2>
          <div style={{ display: 'grid', gap: '16px', maxWidth: '600px' }}>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px' }}>
              <div>
                <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' }}>
                  Sensor Name:
                </label>
                <input 
                  type="text" 
                  placeholder="e.g., TEMP-001" 
                  value={formData.name}
                  onChange={(e) => handleInputChange('name', e.target.value)}
                  style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '14px' }} 
                />
              </div>

              <div>
                <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' }}>
                  Sensor Type:
                </label>
                <select 
                  value={formData.type}
                  onChange={(e) => handleInputChange('type', e.target.value)}
                  style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '14px' }}
                >
                  <option value="Temperature">Temperature</option>
                  <option value="Moisture">Moisture</option>
                  <option value="Humidity">Humidity</option>
                  <option value="Pressure">Pressure</option>
                </select>
              </div>
            </div>

            <div>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' }}>
                Location:
              </label>
              <input 
                type="text" 
                placeholder="e.g., Barn A - Section 1" 
                value={formData.location}
                onChange={(e) => handleInputChange('location', e.target.value)}
                style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '14px' }} 
              />
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px' }}>
              <div>
                <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' }}>
                  X Coordinate:
                </label>
                <input 
                  type="number" 
                  placeholder="0" 
                  value={formData.coordinates.x}
                  onChange={(e) => handleInputChange('coordinates.x', parseInt(e.target.value) || 0)}
                  style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '14px' }} 
                />
              </div>

              <div>
                <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' }}>
                  Y Coordinate:
                </label>
                <input 
                  type="number" 
                  placeholder="0" 
                  value={formData.coordinates.y}
                  onChange={(e) => handleInputChange('coordinates.y', parseInt(e.target.value) || 0)}
                  style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '14px' }} 
                />
              </div>
            </div>

            <div style={{ display: 'flex', gap: '12px', marginTop: '16px' }}>
              <button 
                onClick={editingSensor ? handleUpdateSensor : handleAddSensor}
                disabled={!formData.name || !formData.location}
                style={{ 
                  background: formData.name && formData.location ? '#10b981' : '#9ca3af', 
                  color: 'white', 
                  padding: '12px 20px', 
                  border: 'none', 
                  borderRadius: '6px', 
                  cursor: formData.name && formData.location ? 'pointer' : 'not-allowed',
                  fontSize: '14px',
                  fontWeight: '500'
                }}
              >
                {editingSensor ? 'Update Sensor' : 'Add Sensor'}
              </button>
              <button 
                onClick={cancelEdit}
                style={{ 
                  background: '#6b7280', 
                  color: 'white', 
                  padding: '12px 20px', 
                  border: 'none', 
                  borderRadius: '6px', 
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: '500'
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Sensors List */}
      <div style={{ background: 'white', borderRadius: '12px', border: '1px solid #e5e7eb', overflow: 'hidden' }}>
        <div style={{ padding: '24px', borderBottom: '1px solid #e5e7eb', background: '#f8fafc' }}>
          <h2 style={{ margin: '0' }}>Sensors ({sensors.length})</h2>
        </div>

        <div>
          {sensors.map(sensor => {
            const stats = generateSensorStats(sensor);
            return (
              <div key={sensor.id} style={{ 
                padding: '20px 24px', 
                borderBottom: '1px solid #f3f4f6', 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'flex-start',
                transition: 'background 0.2s ease'
              }}
              onMouseEnter={(e) => e.currentTarget.style.background = '#f8fafc'}
              onMouseLeave={(e) => e.currentTarget.style.background = 'white'}
              >
                <div style={{ flex: 1 }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '12px' }}>
                    <div style={{ 
                      width: '12px', 
                      height: '12px', 
                      borderRadius: '50%', 
                      background: getStatusColor(sensor.status)
                    }}></div>
                    <h3 style={{ margin: '0', fontSize: '18px', fontWeight: '600' }}>{sensor.name}</h3>
                    <span style={{ 
                      background: sensor.type === 'Temperature' ? '#fef3c7' : '#dbeafe', 
                      color: sensor.type === 'Temperature' ? '#92400e' : '#1e40af',
                      padding: '4px 8px', 
                      borderRadius: '12px', 
                      fontSize: '12px',
                      fontWeight: '500'
                    }}>
                      {sensor.type}
                    </span>
                  </div>

                  <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '20px', marginBottom: '16px' }}>
                    <div>
                      <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>Location</div>
                      <div style={{ fontWeight: '500' }}>{sensor.location}</div>
                    </div>
                    <div>
                      <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>Last Reading</div>
                      <div style={{ fontWeight: '500' }}>{sensor.lastReading} {sensor.unit}</div>
                    </div>
                    <div>
                      <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>Battery Level</div>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                        <div style={{ 
                          width: '60px', 
                          height: '8px', 
                          background: '#e5e7eb', 
                          borderRadius: '4px',
                          overflow: 'hidden'
                        }}>
                          <div style={{ 
                            width: `${sensor.batteryLevel}%`, 
                            height: '100%', 
                            background: getBatteryColor(sensor.batteryLevel),
                            transition: 'width 0.3s ease'
                          }}></div>
                        </div>
                        <span style={{ fontSize: '14px', fontWeight: '500' }}>{sensor.batteryLevel}%</span>
                      </div>
                    </div>
                  </div>

                  <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(120px, 1fr))', gap: '16px', fontSize: '14px', color: '#6b7280' }}>
                    <div><strong>Install Date:</strong> {new Date(sensor.installDate).toLocaleDateString()}</div>
                    <div><strong>Days Online:</strong> {stats.daysOnline}</div>
                    <div><strong>Total Readings:</strong> {stats.totalReadings.toLocaleString()}</div>
                    <div><strong>Accuracy:</strong> {stats.accuracy}%</div>
                    <div><strong>Uptime:</strong> {stats.uptime}%</div>
                    <div><strong>Alerts:</strong> {sensor.alertsCount}</div>
                  </div>

                  {selectedSensor === sensor.id && (
                    <div style={{ 
                      marginTop: '16px', 
                      padding: '16px', 
                      background: '#f0f9ff', 
                      borderRadius: '8px',
                      border: '1px solid #bae6fd'
                    }}>
                      <h4 style={{ margin: '0 0 12px 0', color: '#0c4a6e' }}>Detailed Statistics</h4>
                      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '12px', fontSize: '14px' }}>
                        <div><strong>Coordinates:</strong> ({sensor.coordinates.x}, {sensor.coordinates.y})</div>
                        <div><strong>Daily Readings:</strong> {stats.avgDaily}</div>
                        <div><strong>Status:</strong> <span style={{ color: getStatusColor(sensor.status), fontWeight: '600', textTransform: 'capitalize' }}>{sensor.status}</span></div>
                        <div><strong>Last Update:</strong> {sensor.lastUpdate}</div>
                      </div>
                    </div>
                  )}
                </div>

                <div style={{ display: 'flex', gap: '8px', marginLeft: '20px' }}>
                  <button
                    onClick={() => setSelectedSensor(selectedSensor === sensor.id ? null : sensor.id)}
                    style={{
                      background: '#f3f4f6',
                      color: '#374151',
                      border: 'none',
                      padding: '8px 12px',
                      borderRadius: '6px',
                      cursor: 'pointer',
                      fontSize: '12px',
                      fontWeight: '500'
                    }}
                  >
                    {selectedSensor === sensor.id ? 'Hide Stats' : 'View Stats'}
                  </button>
                  <button
                    onClick={() => handleEditSensor(sensor)}
                    style={{
                      background: '#3b82f6',
                      color: 'white',
                      border: 'none',
                      padding: '8px 12px',
                      borderRadius: '6px',
                      cursor: 'pointer',
                      fontSize: '12px',
                      fontWeight: '500'
                    }}
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDeleteSensor(sensor.id)}
                    style={{
                      background: '#ef4444',
                      color: 'white',
                      border: 'none',
                      padding: '8px 12px',
                      borderRadius: '6px',
                      cursor: 'pointer',
                      fontSize: '12px',
                      fontWeight: '500'
                    }}
                  >
                    Delete
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default SensorManagement;
