import React, { useState, useEffect } from 'react';

const MyFarm = () => {
  const [farmData, setFarmData] = useState({
    name: 'Springfield Farm',
    owner: 'John Farmer',
    location: 'Springfield, Illinois, USA',
    coordinates: {
      latitude: 39.7817,
      longitude: -89.6501
    },
    establishedYear: 2018,
    totalArea: 245,
    cropTypes: ['Hay', 'Corn', 'Soybeans'],
    sensors: 12,
    barns: 3,
    fields: 5
  });

  const [weatherHistory, setWeatherHistory] = useState([]);
  const [currentWeather, setCurrentWeather] = useState({});
  const [forecast, setForecast] = useState([]);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    // Generate mock weather history for last 7 days
    const generateWeatherHistory = () => {
      const history = [];
      const today = new Date();

      for (let i = 6; i >= 0; i--) {
        const date = new Date(today.getTime() - i * 24 * 60 * 60 * 1000);
        const baseTemp = 22 + Math.sin((date.getMonth() + 1) * 0.5) * 8; // Seasonal variation

        history.push({
          date: date.toLocaleDateString(),
          dayName: date.toLocaleDateString('en-US', { weekday: 'short' }),
          high: Math.round(baseTemp + Math.random() * 6),
          low: Math.round(baseTemp - Math.random() * 8),
          humidity: Math.round(45 + Math.random() * 30),
          precipitation: Math.random() > 0.7 ? Math.round(Math.random() * 15) : 0,
          windSpeed: Math.round(5 + Math.random() * 15),
          condition: Math.random() > 0.3 ? 'Sunny' : Math.random() > 0.5 ? 'Cloudy' : 'Rainy'
        });
      }

      setWeatherHistory(history);

      // Set current weather as today's data
      const today_weather = history[history.length - 1];
      setCurrentWeather({
        temperature: today_weather.high,
        condition: today_weather.condition,
        humidity: today_weather.humidity,
        windSpeed: today_weather.windSpeed,
        icon: today_weather.condition === 'Sunny' ? 'â˜€ï¸' : today_weather.condition === 'Cloudy' ? 'â›…' : 'ðŸŒ§ï¸'
      });
    };

    // Generate 5-day forecast
    const generateForecast = () => {
      const forecastData = [];
      const today = new Date();

      for (let i = 1; i <= 5; i++) {
        const date = new Date(today.getTime() + i * 24 * 60 * 60 * 1000);
        const baseTemp = 22 + Math.sin((date.getMonth() + 1) * 0.5) * 8;

        forecastData.push({
          date: date.toLocaleDateString(),
          dayName: date.toLocaleDateString('en-US', { weekday: 'short' }),
          high: Math.round(baseTemp + Math.random() * 6),
          low: Math.round(baseTemp - Math.random() * 8),
          condition: Math.random() > 0.3 ? 'Sunny' : Math.random() > 0.5 ? 'Cloudy' : 'Rainy',
          icon: Math.random() > 0.3 ? 'â˜€ï¸' : Math.random() > 0.5 ? 'â›…' : 'ðŸŒ§ï¸',
          precipitation: Math.random() > 0.6 ? Math.round(Math.random() * 20) : 0
        });
      }

      setForecast(forecastData);
    };

    generateWeatherHistory();
    generateForecast();
  }, []);

  const handleSave = () => {
    setIsEditing(false);
    // Here you would normally save to backend
  };

  const handleCancel = () => {
    setIsEditing(false);
    // Reset form data
  };

  return (
    <div style={{ padding: '32px 24px', maxWidth: '1400px', margin: '0 auto' }}>
      <div style={{ marginBottom: '32px' }}>
        <h1>My Farm</h1>
        <p style={{ color: '#6b7280' }}>Farm information, location details, and weather monitoring</p>
      </div>

      <div style={{ display: 'grid', gap: '24px', gridTemplateColumns: '2fr 1fr', marginBottom: '32px' }}>
        {/* Farm Information */}
        <div style={{ background: 'white', padding: '24px', borderRadius: '12px', border: '1px solid #e5e7eb' }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px' }}>
            <h2>Farm Details</h2>
            {!isEditing ? (
              <button 
                onClick={() => setIsEditing(true)}
                style={{ background: '#3b82f6', color: 'white', padding: '8px 16px', border: 'none', borderRadius: '6px', cursor: 'pointer' }}
              >
                Edit Details
              </button>
            ) : (
              <div style={{ display: 'flex', gap: '8px' }}>
                <button 
                  onClick={handleSave}
                  style={{ background: '#10b981', color: 'white', padding: '8px 16px', border: 'none', borderRadius: '6px', cursor: 'pointer' }}
                >
                  Save
                </button>
                <button 
                  onClick={handleCancel}
                  style={{ background: '#6b7280', color: 'white', padding: '8px 16px', border: 'none', borderRadius: '6px', cursor: 'pointer' }}
                >
                  Cancel
                </button>
              </div>
            )}
          </div>

          <div style={{ display: 'grid', gap: '20px', gridTemplateColumns: '1fr 1fr' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' }}>
                Farm Name
              </label>
              {isEditing ? (
                <input 
                  type="text"
                  value={farmData.name}
                  onChange={(e) => setFarmData(prev => ({...prev, name: e.target.value}))}
                  style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px' }}
                />
              ) : (
                <p style={{ margin: '0', padding: '12px', background: '#f9fafb', borderRadius: '6px' }}>{farmData.name}</p>
              )}
            </div>

            <div>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' }}>
                Owner
              </label>
              {isEditing ? (
                <input 
                  type="text"
                  value={farmData.owner}
                  onChange={(e) => setFarmData(prev => ({...prev, owner: e.target.value}))}
                  style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px' }}
                />
              ) : (
                <p style={{ margin: '0', padding: '12px', background: '#f9fafb', borderRadius: '6px' }}>{farmData.owner}</p>
              )}
            </div>

            <div style={{ gridColumn: '1 / -1' }}>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' }}>
                Location
              </label>
              {isEditing ? (
                <input 
                  type="text"
                  value={farmData.location}
                  onChange={(e) => setFarmData(prev => ({...prev, location: e.target.value}))}
                  style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px' }}
                />
              ) : (
                <p style={{ margin: '0', padding: '12px', background: '#f9fafb', borderRadius: '6px' }}>{farmData.location}</p>
              )}
            </div>

            <div>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' }}>
                Latitude
              </label>
              {isEditing ? (
                <input 
                  type="number"
                  step="0.0001"
                  value={farmData.coordinates.latitude}
                  onChange={(e) => setFarmData(prev => ({...prev, coordinates: {...prev.coordinates, latitude: parseFloat(e.target.value)}}))}
                  style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px' }}
                />
              ) : (
                <p style={{ margin: '0', padding: '12px', background: '#f9fafb', borderRadius: '6px' }}>{farmData.coordinates.latitude}Â°</p>
              )}
            </div>

            <div>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' }}>
                Longitude
              </label>
              {isEditing ? (
                <input 
                  type="number"
                  step="0.0001"
                  value={farmData.coordinates.longitude}
                  onChange={(e) => setFarmData(prev => ({...prev, coordinates: {...prev.coordinates, longitude: parseFloat(e.target.value)}}))}
                  style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px' }}
                />
              ) : (
                <p style={{ margin: '0', padding: '12px', background: '#f9fafb', borderRadius: '6px' }}>{farmData.coordinates.longitude}Â°</p>
              )}
            </div>

            <div>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' }}>
                Total Area (acres)
              </label>
              {isEditing ? (
                <input 
                  type="number"
                  value={farmData.totalArea}
                  onChange={(e) => setFarmData(prev => ({...prev, totalArea: parseInt(e.target.value)}))}
                  style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px' }}
                />
              ) : (
                <p style={{ margin: '0', padding: '12px', background: '#f9fafb', borderRadius: '6px' }}>{farmData.totalArea} acres</p>
              )}
            </div>

            <div>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' }}>
                Established Year
              </label>
              {isEditing ? (
                <input 
                  type="number"
                  value={farmData.establishedYear}
                  onChange={(e) => setFarmData(prev => ({...prev, establishedYear: parseInt(e.target.value)}))}
                  style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '6px' }}
                />
              ) : (
                <p style={{ margin: '0', padding: '12px', background: '#f9fafb', borderRadius: '6px' }}>{farmData.establishedYear}</p>
              )}
            </div>
          </div>
        </div>

        {/* Current Weather */}
        <div style={{ background: 'white', padding: '24px', borderRadius: '12px', border: '1px solid #e5e7eb' }}>
          <h2>Current Weather</h2>
          <div style={{ textAlign: 'center', marginBottom: '20px' }}>
            <div style={{ fontSize: '60px', marginBottom: '12px' }}>{currentWeather.icon}</div>
            <h3 style={{ margin: '0 0 8px 0', fontSize: '2rem' }}>{currentWeather.temperature}Â°C</h3>
            <p style={{ margin: '0 0 16px 0', color: '#6b7280', fontSize: '1.1rem' }}>{currentWeather.condition}</p>
          </div>

          <div style={{ display: 'grid', gap: '12px' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderBottom: '1px solid #f3f4f6' }}>
              <span style={{ color: '#6b7280' }}>Humidity:</span>
              <span style={{ fontWeight: '500' }}>{currentWeather.humidity}%</span>
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderBottom: '1px solid #f3f4f6' }}>
              <span style={{ color: '#6b7280' }}>Wind Speed:</span>
              <span style={{ fontWeight: '500' }}>{currentWeather.windSpeed} km/h</span>
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0' }}>
              <span style={{ color: '#6b7280' }}>Coordinates:</span>
              <span style={{ fontWeight: '500', fontSize: '0.9rem' }}>{farmData.coordinates.latitude}Â°, {farmData.coordinates.longitude}Â°</span>
            </div>
          </div>
        </div>
      </div>

      {/* Farm Statistics */}
      <div style={{ background: 'white', padding: '24px', borderRadius: '12px', border: '1px solid #e5e7eb', marginBottom: '32px' }}>
        <h2 style={{ marginBottom: '24px' }}>Farm Overview</h2>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '24px' }}>
          <div style={{ textAlign: 'center', padding: '20px', background: '#f8fafc', borderRadius: '8px' }}>
            <div style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#3b82f6', marginBottom: '8px' }}>{farmData.sensors}</div>
