// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Landing from './components/Landing/Landing';
import './App.css';

// Components
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import MyFarm from './components/MyFarm/MyFarm';
import Sensors from './components/Sensors/Sensors';
import SensorDetails from './components/Sensors/SensorDetails';
import Reports from './components/Reports/Reports';
import Team from './components/Team/Team';
import Alerts from './components/Alerts/Alerts';
import Profile from './components/Profile/Profile';

// Services
import { authService } from './services/authService';
import { userService } from './services/userService';

// All Context Providers
import { DataProvider } from './contexts/DataContext';
import { FarmProvider } from './contexts/FarmContext';
import { UserProvider } from './contexts/UserContext';
import { NotificationProvider } from './contexts/NotificationContext';
import { SensorsProvider } from './contexts/SensorsContext';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [mobileOpen, setMobileOpen] = useState(false);

  useEffect(() => {
    const initializeAuth = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          const userData = await userService.getCurrentUser();
          setUser(userData);
        }
      } catch (error) {
        console.error('Auth initialization failed:', error);
        localStorage.removeItem('token');
      } finally {
        setLoading(false);
      }
    };

    initializeAuth();
  }, []);

  const handleLogin = async (email, password) => {
    try {
      const response = await authService.login(email, password);
      setUser(response.user);
      localStorage.setItem('token', response.token);
      return { success: true };
    } catch (error) {
      return { 
        success: false, 
        error: error.response?.data?.message || 'Login failed' 
      };
    }
  };

  const handleLogout = () => {
    authService.logout();
    setUser(null);
    localStorage.removeItem('token');
  };

  const handleToggleSidebar = () => {
    if (window.innerWidth <= 1024) {
      // Mobile: toggle mobile overlay
      setMobileOpen(!mobileOpen);
    } else {
      // Desktop: toggle collapsed state
      setSidebarCollapsed(!sidebarCollapsed);
    }
  };

  const handleCloseMobile = () => {
    setMobileOpen(false);
  };

  if (loading) {
    return (
      <div className="app-loading">
        <div className="loading-spinner">
          <div className="hayguard-logo">
            <img src="/logo.png" alt="HayGuard" />
          </div>
          <div className="loading-text">Loading HayGuard...</div>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <Router>
        <div className="app">
          <Routes>
            <Route path="/" element={<Landing onLogin={handleLogin} />} />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </div>
      </Router>
    );
  }

  return (
    <Router>
      <NotificationProvider>
        <UserProvider>
          <FarmProvider>
            <SensorsProvider>
              <DataProvider>
                <div className="app">
                  <Sidebar
                    collapsed={sidebarCollapsed}
                    onToggle={handleToggleSidebar}
                    user={user}
                    mobileOpen={mobileOpen}
                    onCloseMobile={handleCloseMobile}
                  />

                  <div className={`main-content ${sidebarCollapsed ? 'sidebar-collapsed' : ''}`}>
                    <Header
                      user={user}
                      onLogout={handleLogout}
                      onToggleSidebar={handleToggleSidebar}
                    />

                    <main className="app-main">
                      <Routes>
                        <Route path="/" element={<Navigate to="/dashboard" replace />} />
                        <Route path="/dashboard" element={<Dashboard user={user} />} />
                        <Route path="/sensors" element={<Sensors />} />
                        <Route path="/sensors/:id" element={<SensorDetails />} />
                        <Route path="/reports" element={<Reports />} />
                        <Route path="/team" element={<Team />} />
                        <Route path="/alerts" element={<Alerts />} />
                        <Route path="/my-farm" element={<MyFarm />} />
                        <Route path="/my-profile" element={<Profile />} />
                        <Route path="*" element={<Navigate to="/dashboard" replace />} />
                      </Routes>
                    </main>
                  </div>
                </div>
              </DataProvider>
            </SensorsProvider>
          </FarmProvider>
        </UserProvider>
      </NotificationProvider>
    </Router>
  );
}

export default App;
